use pipeline::Pipeline;

pub fn make_pipeline() -> Pipeline {
    Pipeline {
        queue: vec![
            ("trimmer-es".into(), trimmer),
            ("stopWordFilter-es".into(), stop_word_filter),
            ("stemmer-es".into(), stemmer),
        ],
    }
}

